import * as React from 'react';
import Ikon from 'nav-frontend-ikoner-assets';
import ButtonBase, { ButtonBaseProps } from 'buttons';

const HamburgerButton = React.forwardRef<HTMLButtonElement, ButtonBaseProps>((props, ref) => {
    return (
        <ButtonBase ref={ref} type="flat" kompakt {...props}>
            <Ikon kind="hamburger" />
            {props.children || <span className="sr-only">Meny</span>}
        </ButtonBase>
    );
});
export default HamburgerButton;
